import React, { Component } from 'react';
import {Modal, ModalHeader, ModalBody, ModalFooter,Inputm,Button } from 'reactstrap';
import { connect } from 'react-redux';

class Modals extends Component {
    constructor(props){
        super(props);
        this.state={
            data : {
              id : "",
              pass : ""
            }
        }
    }

    handleChangeId = (e) => {
        this.setState({
          data : {...this.state.data, id:e.target.value} 
        })
      }

    handleChangePass = (e) => {
        this.setState({
          data : {...this.state.data, pass:e.target.value} 
        })
      }

    render() {
        return (
            <div>
                <Modal isOpen={this.props.modal}>
                    <ModalHeader toggle={this.toggleModal}>Đăng nhập</ModalHeader>
                    <ModalBody>
                      <p>Tên đăng nhập</p>
                      <Input onChange = {this.handleChangeId} type="text"></Input>
                      <p className="mt-3">Mật khẩu</p>
                      <Input onChange = {this.handleChangePass} type="password"></Input>
                    </ModalBody>
                    <ModalFooter>
                        <Button color="primary" onClick={this.props.handleSubmitt}>Đăng nhập</Button>
                        <Button color="danger" onClick={this.toggleModal}>Hủy</Button>
                    </ModalFooter>
                </Modal>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        isOpen : state.statusModal
    }
}

