import React, { Component } from 'react';
import {Modal, ModalHeader, ModalBody, ModalFooter,Input,Button } from 'reactstrap';
import axios from 'axios';
import config from './../../config/index'

class ModalsFile extends Component {
    constructor(props){
        super(props);
        this.state = {
            fileimages : [],
            imagePreviewUrl : [],
        }
    }

    handeleFileChosen = (e) => {
        let reader = new FileReader();
        let file = e.target.files[0];

        e.preventDefault();
        reader.onloadend = () => {
            this.setState({
                fileimages: [...this.state.fileimages, file],
                //imagePreviewUrl: [...this.state.imagePreviewUrl, reader.result]
            });
          }

        if(file) reader.readAsDataURL(file);
    }

    handleSubmit = (e) => {
        let formData = new FormData();
        this.state.fileimages.map((item, index) => (
            formData.append('file', item, item.name)
        ))

        axios({
            url: `${config.baseUrl}/api/${this.props.url}`,
            method: `post`,
            headers: {
                'content-type': 'multipart/form-data'
            },
            data : formData
            })
            .then((response) => {
              console.log(response.data);
            })
            .catch((error) => console.log(error));
    }

    render() {
        return (
            <div>
                <Modal isOpen={this.props.isOpen}>
                    <ModalHeader toggle={this.toggleModal}>{this.props.title}</ModalHeader>
                    <ModalBody>
                      <Input type="file" onChange={this.handeleFileChosen}></Input>
                    </ModalBody>
                    <ModalFooter>
                        <Button color="primary" onClick={this.handleSubmit}>Upload</Button>
                        <Button color="danger" onClick={this.props.isToggle}>Há»§y</Button>
                    </ModalFooter>
                </Modal>
            </div>
        );
    }
}

export default ModalsFile;
