import * as types from './../constants/ActionTypes'
import config from './../config/index'
import axios from 'axios';

let initialState = [
  {
      "id": 42,
      "model": 1,
      "good": 0
  },
  {
      "id": 41,
      "model": 0,
      "good": 1
  },
  {
      "id": 37,
