      "model": 0,
      "good": 1
  },
  {
      "id": 1,
      "model": 0,
      "good": 1
  }
];

let myReducer = (state = initialState,action) => {
    const newState = {...state}
    switch(action.type){
        case types.SHOW_ALL:
            state = action.data;
            return state;
        case types.SHOW_MODEL:
            state = action.data.filter((item,index)=>{
                if(item.model === 1) {
                    return true
                }  
                else return false;
                })
                return state;
        case types.SHOW_GOOD:
                state = action.data.filter((item,index)=>{
                    if(item.good === 1) {
                      return true
                    }  
                    else return false;
                  })
                return state;
        default : return state;
    }
}

export default myReducer;
