import React, { Component } from 'react';
import {Modal, ModalHeader, ModalBody, ModalFooter,Input,Button } from 'reactstrap';
import { connect } from 'react-redux';
import * as actions from './../../actions/index';


class ModalsFile extends Component {
    constructor(props){
        super(props);
        this.state = {
            fileimages : [],
        }
    }

    handeleFileChosen = (e) => {
        let reader = new FileReader();
        let file = e.target.files[0];

        e.preventDefault();
        reader.onloadend = () => {
            this.setState({
                fileimages: [...this.state.fileimages, file],
            });
          }

        if(file) reader.readAsDataURL(file);
    }

    handleSubmit = (e) => {
        let formData = new FormData();
        this.state.fileimages.map((item, index) => (
            formData.append('file', item, item.name)
        ))
            formData.append('temp',this.props.temp);

        this.props.handleSubmit(formData,this.props.url);
    }

    render() {
        return (
            <div>
                <Modal isOpen={this.props.isOpen}>
                    <ModalHeader toggle={this.toggleModal}>{this.props.title}</ModalHeader>
                    <ModalBody>
                      <Input type="file" onChange={this.handeleFileChosen}></Input>
                    </ModalBody>
                    <ModalFooter>
                        <Button color="primary" onClick={this.handleSubmit}>Upload</Button>
                        <Button color="danger" onClick={this.props.isToggleModalsFile}>Há»§y</Button>
                    </ModalFooter>
                </Modal>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        isOpen : state.modalsFile.status,
        title : state.modalsFile.title,
        url : state.modalsFile.url,
        temp : state.modalsFile.temp,
    }
}

const mapDispatchToProps = (dispatch, props) => {
    return {
        isToggleModalsFile : ()=>{
            dispatch(actions.modalsFile())
        },
        handleSubmit : (formData,url)=>{
            dispatch(actions.callApiUpload(formData,url));
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(ModalsFile);
