import React, { Component } from 'react';
import {connect} from 'react-redux';
import * as actions from './../actions/index';
import ModalsFile from './helpers/ModalsFile';
import {
    Collapse,
    Navbar,
    NavbarToggler,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink} from 'reactstrap';
class OptionMenu extends Component {
    constructor(props){
        super(props);
        this.state = {
            isOpen : false,
            isOpenModal : false,
        }
    }

    toggle = () => {
        this.setState = {
            isOpen : !this.state.isOpen
        }
    }

    toggleModal = () => {
        console.log('dm')
        this.setState = {
            isOpenModal : !this.state.isOpenModal
        }
    }

    render() {
        console.log(this.state.isOpenModal)
        return (
            <div className="container themes-body">
                <Navbar light expand="md">
                    <NavbarBrand >Kho Giao Diện</NavbarBrand>
                    <NavbarToggler onClick={this.toggle} />
                    <Collapse isOpen={this.state.isOpen} navbar>
                        <Nav className="ml-auto" navbar>
                            <NavItem>
                                <NavLink onClick={this.props.showAll}>Tất cả</NavLink>
                            </NavItem>
                            <NavItem>
                                <NavLink onClick={this.props.showModel}>Bán Chạy Nhất</NavLink>
                            </NavItem>
                            <NavItem>
                                <NavLink onClick={this.props.showGood}>Đánh giá tốt nhất</NavLink>
                            </NavItem>
                            {this.props.onSingIn ?
                                (
                                <NavItem>
                                    <NavLink onClick={this.toggleModal}>Thêm</NavLink>
                                </NavItem>
                                )    
                            : null}
                            {this.props.onSingIn ?
                                (
                                <NavItem>
                                    <NavLink onClick={this.props.showGood}>Xóa</NavLink>
                                </NavItem>
                                )    
                            : null}                   

                         </Nav>
                    </Collapse>
                </Navbar>
                <ModalsFile isOpen = {this.state.isOpenModal} title="Thêm danh sách" url="upload-image" isToggle = {this.toggleModal}></ModalsFile>
            </div>
        );
    }
}

const mapStateToProps = (state) =>{
    return {
        onSingIn : state.singIn
    }
}

const mapDispatchToProps = (dispatch, props) => {
    return {
      showAll : () => {
        dispatch(actions.showAll())
      },
      showGood : () => {
        dispatch(actions.showGood())
      },
      showModel : () => {
        dispatch(actions.showModel())
      }, 
    }
  }

  export default connect(mapStateToProps,mapDispatchToProps)(OptionMenu);
