import * as types from './../constants/ActionTypes'
import config from './../config/index'
import axios from 'axios';

let initialState = []
axios({
    url: `${config.baseUrl}/api/get-all`,
    method: 'get'
    })
    .then((response) => {
      initialState = response.data;
    })
    .catch((error) => {
        let initialState = [];
    });

let myReducer = (state = initialState,action) => {

    let newState = [...initialState];
    let listArray = [...initialState];
    switch(action.type){
        case types.SHOW_ALL:
                console.log(state);
                return newState;
        case types.SHOW_MODEL:
            console.log(state);
            listArray = newState.filter((item,index)=>{
                if(item.model === 1) {
                    return true
                }  
                else return false;
                })
                console.log("new",state);
                return listArray;
        case types.SHOW_GOOD:
                console.log(state);
            listArray = newState.filter((item,index)=>{
                if(item.good === 1) {
                    return true
                }  
                else return false;
                })
                return listArray;
        default : return newState;
    }
}

export default myReducer;
