import { takeLatest, call, put } from 'redux-saga/effects'
import axios from 'axios';
import config from "./"

function signupApi(id, pass){
  return axios({
    method: "POST",
    url: "http(s)://5af675a576035f0014f2bcc3.mockapi.io/api/"
  });
}

function* signupFlow(action){
  const { id, pass } = action.data
  try {
    const response = yield call(signupApi, id, pass) 
    yield put({ type: "SIGNUP_SUCCESS", response })
  } catch(error){
    yield put({ type: "SIGNUP_ERROR", error })
  }
}

export function* watchSingIn() {
  yield takeLatest("CALL-API-SINGIN", signupFlow);
}
