import React, { Component } from 'react';
import {Modal, ModalHeader, ModalBody, ModalFooter,Input,Button } from 'reactstrap';
import axios from 'axios';
import config from './../../config/index'

class ModalsFile extends Component {
    constructor(props){
        super(props);
    }

    handeleFileChosen = (event) => {
        const data = new FormData()
        data.append('file', event.target.files[0])
        data.append('name', 'some value user types')
        data.append('description', 'some value user types')
        axios.post(`{}/api/edit-file`, data).then((response) => {
          this.setState({
            imageUrl: response.data.fileUrl
          })
        })
    }
    render() {
        return (
            <div>
                <Modal isOpen={this.props.isOpen}>
                    <ModalHeader toggle={this.toggleModal}>{this.props.title}</ModalHeader>
                    <ModalBody>
                      <Input onChange = {this.handleChangePass} type="file" onChange={this.handeleFileChosen}></Input>
                    </ModalBody>
                    <ModalFooter>
                        <Button color="primary" onClick={this.handleSubmit}>Sửa</Button>
                        <Button color="danger" onClick={this.props.isToggle}>Hủy</Button>
                    </ModalFooter>
                </Modal>
            </div>
        );
    }
}

export default ModalsFile;
