import * as types from './../constants/ActionTypes'

let initialState = [
    {
        "id": 0,
        "name": "helloworldpng",
        "path": "public/images/upload_d7babf2cb74c50d59bae503abb0fcb00",
        "imageUrl": "images/upload_d7babf2cb74c50d59bae503abb0fcb00",
        "model": 0,
        "good": 1,
        "createAt": "2019-7-17"
    },
    {
        "id": 1,
        "name": "themes.jpeg",
        "path": "public/images/upload_93ba5f52ba550f332e2bec0500f687c5.jpeg",
        "imageUrl": "images/upload_93ba5f52ba550f332e2bec0500f687c5.jpeg",
        "model": 1,
        "good": 0,
        "createAt": "2019-7-17"
    },
    {
        "id": 2,
        "name": "themes2.jpeg",
        "path": "public/images/upload_0a92a7128ba804f399f8599f3d643a8c.jpeg",
        "imageUrl": "images/upload_0a92a7128ba804f399f8599f3d643a8c.jpeg",
        "model": 0,
        "good": 1,
        "createAt": "2019-7-17"
    }
]

let myReducer = (state = initialState,action) => {

    let newState = [...initialState];
    let listArray = initialState;
    switch(action.type){
        case types.SHOW_DATA:
            initialState = [...initialState,action.data];
            return initialState;
        case types.UPLOAD_SUCCESS:
            if(action.data.message === "upload-success"){
                initialState = [...initialState,action.data.data]
            }
            else if(action.data.message === "edit-success"){
                initialState[action.data.temp] = action.data.data;
            }
            return initialState;
        case types.DEL_SUCCESS :
            if(action.data.message === "delete-success"){
                action.listData.forEach((element,index) => {
                    initialState.splice(element-index,1)
                });
            }
            return initialState;

        case types.SHOW_ALL:
            return newState;
        case types.SHOW_MODEL:
            listArray = newState.filter((item,index)=>{
                if(item.model === 1) {
                    return true
                }  
                else return false;
                })
                return listArray;
        case types.SHOW_GOOD:
            listArray = newState.filter((item,index)=>{
                if(item.good === 1) {
                    return true
                }  
                else return false;
                })
                return listArray;
        default : return newState;
    }
}

export default myReducer;
