import * as types from './../constants/ActionTypes'

let initialState = [];
let myReducer = (state = initialState,action) => {
    switch(action.type){
        case types.SHOW_ALL:
            return action.data
        case types.SHOW_MODEL:
            state = action.data.filter((item,index)=>{
                if(item.model === 1) {
                    return true
                }  
                else return false;
                })
                return state;
        case types.SHOW_GOOD:
            state = action.data.filter((item,index)=>{
                if(item.good === 1) {
                    return true
                }  
                else return false;
                })
                return state;
        default : return action.data;
    }
}

export default myReducer;
