import React, { Component } from 'react';
import {connect} from 'react-redux';
import ModalsFile from './helpers/ModalsFile';

class Box extends Component {
    constructor(props){
        super(props);
        this.state={
          isOpen : false,
          temp : 0
        }
    }
    toggleModal = ()=>{
      this.setState({
        
        isOpen: !this.state.isOpen
      })
    }

    render() {
        return (
          <div className="themes container">
          <div className="row">
            {this.props.dataRender.map((item,index) => (
              <div key={index} className="col-xl-3 col-lg-3 col-md-4 col-sm-6" >
                <div className="box">
                  <div className="img_prodcution">
                    {/* <img src={`https://sorano.vn/cloud/timthumb.php?src=https://sorano.vn/cloud/cdn/product/${item.id}.png&h=auto&w=555&zc=zc=1`} title="Mẫu giao diện 42" alt="Mẫu giao diện 42"></img> */}
                    <img src={`http://localhost:3001/api/open-image?image_name=${item.path}`} title="Mẫu giao diện 42" alt="Mẫu giao diện 42"></img>
                  </div>
                <div className="view container btn-group-vertical" /*style={{display:  this.state.showStore === index ? 'block' : 'inline-flex' }}*/>
                  <button className="btn btn-danger d-block my-3 w-75 mx-auto">Xem DeMo</button>
                  <button className="btn btn-primary d-block w-75 mx-auto">Xem Chi Tiet</button>
                  {this.props.onSingIn ? <button className="btn btn-danger d-block mt-3 w-75 mx-auto" onClick={this.toggleModal(key)}>Sửa</button> : null}                  
                </div>
                </div>
                <div><b>Mẫu Giao Diện 1</b></div>
            </div>
              ))}
          </div>
          <div className="row justify-content-center my-5">
            <button className="btn btn-primary px-4 py-2">Xem Tất Cả Các Màu</button>
          </div>
          <ModalsFile isOpen = {this.state.isOpen} title="Sửa danh sách" url="edit-image" isToggle = {this.toggleModal}></ModalsFile>
        </div>
            
        );
    }
}

const mapStateToProps = (state) => {
  return {
    dataRender : state.sort,
    onSingIn : state.singIn
  }
}

export default connect(mapStateToProps,null) (Box);
