import * as types from './../constants/ActionTypes'
import config from './../config/index'
import axios from 'axios';

let initialState = [
  {
      "id": 42,
      "model": 1,
      "good": 0
  },
  {
      "id": 41,
      "model": 0,
      "good": 1
  },
  {
      "id": 37,
      "model": 1,
      "good": 1
  },
  {
      "id": 27,
      "model": 1,
      "good": 0
  },
  {
      "id": 21,
      "model": 0,
      "good": 1
  },
  {
      "id": 20,
      "model": 1,
      "good": 1
  },
  {
      "id": 18,
      "model": 0,
      "good": 1
  },
  {
      "id": 1,
      "model": 0,
      "good": 1
  }
];

let myReducer = (state = initialState,action) => {

    switch(action.type){
        case types.SHOW_ALL:
            state = action.data;
            return state;
        case types.SHOW_MODEL:
            state = action.data.filter((item,index)=>{
                if(item.model === 1) {
                    return true
                }  
                else return false;
                })
                return state;
        case types.SHOW_GOOD:
                state = action.data.filter((item,index)=>{
                    if(item.good === 1) {
                      return true
                    }  
                    else return false;
                  })
                return state;
        default : return state;
    }
}

export default myReducer;
